import { useParams } from 'react-router-dom'


function EditDraft(){

    const { id } = useParams();

    const [drafts, setDrafts] = useState([])
    useEffect(() => {
        fetch(`/drafts/${id}`)
        .then((res) => res.json())
        .then((data) => {
            setDrafts(data)            
        })
    }, [])




    return(
        <>




        </>
    )
}
export default EditDraft;
