import React, {} from "react";



function Calendar(){
    const [isAuthenticated, setIsAuthenticated] = useState(false);


  const gapi = window.gapi
  /* 
    Update with your own Client Id and Api key 
  */
  const CLIENT_ID = "401422596140-41k5anood1vak9iuol4vrlrv5au0ejch.apps.googleusercontent.com"
  const API_KEY = "AIzaSyBivuXBGV5wgbf--W8IUI84lY_f6_c40xA"
  const DISCOVERY_DOCS = ["https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest"]
  const SCOPES = 'https://www.googleapis.com/auth/calendar';


  function handleClick(){
    const onLoginSuccess = (response) => {
        setIsAuthenticated(true);
        const { access_token } = response.tokenObj;

        // Load the calendar API
        window.gapi.client.load('calendar', 'v3', () => {
          // Create a new calendar event
          const event = {
            summary: 'Example Event',
            start: {
              dateTime: '2023-04-17T10:00:00-07:00',
              timeZone: 'America/Los_Angeles',
            },
            end: {
              dateTime: '2023-04-17T11:00:00-07:00',
              timeZone: 'America/Los_Angeles',
            },
            reminders: {
              useDefault: true,
            },
          };

          // Call the Calendar API to insert the event
          const request = window.gapi.client.calendar.events.insert({
            calendarId: 'primary',
            resource: event,
          });

          request.execute((event) => {
            console.log(`Event created: ${event.htmlLink}`);
          });
        });
      };

      const onLoginFailure = () => {
        setIsAuthenticated(false);
      };
}

    return(
        <>
        <button  type="button" className="btn btn-success calendar" onClick={handleClick}>SET REMINDER</button>        

        </>
    )
}
export default Calendar;
