import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom'

const DraftsContext = React.createContext();

function DraftsProvider({ children }) {
  const { id } = useParams();
  const [drafts, setDrafts] = useState([]);

  useEffect(() => {
    fetch(`/drafts/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setDrafts(data);
      });
  }, []);

  return (
    <DraftsContext.Provider value={{ drafts, setDrafts }}>
      {children}
    </DraftsContext.Provider>
  );
}

export { DraftsContext, DraftsProvider };
