import React from "react";
import { NavLink } from "react-router-dom";
import '../css/EventCard.css';


function EventCard({ event }) {
    const {id, image, title, date, tickets} = event

    function truncate(str, n) {
        return str?.length > n ? str.substr(0, n - 1) + "..." : str;
      }

    const dates = new Date(date);
    const dayInWords = dates.toLocaleString("default", { weekday: "long" }); // "Tuesday"
    const day = dates.toLocaleString("default", { day: "numeric" }); // "15"
    const monthInWords = dates.toLocaleString("default", { month: "long" }); // "June"


    function getOrdinalSuffix(day) {
        const j = day % 10, k = day % 100;
        if (j === 1 && k !== 11) {
          return "st";
        }
        if (j === 2 && k !== 12) {
          return "nd";
        }
        if (j === 3 && k !== 13) {
          return "rd";
        }
        return "th";
      }


      function capitalizeFirstThreeWords(str) {
        const words = str.split(' '); // split the string into an array of words
        const firstThreeWords = words.slice(0, 3); // get the first 3 words
        const capitalizedWords = firstThreeWords.map(word => word.charAt(0).toUpperCase() + word.slice(1)); // capitalize the first letter of each word
        return capitalizedWords.join(' '); // join the words back into a string
      }

    return (
        <>
            <div className="card " style={{ width: '18rem'}}>
            <NavLink  to={`/events/${event.id}`}>
                <img src={image} className="card-img-top"alt={image} />
                </NavLink>
                <div className="card-body">
                    <div>
                        <p className="card-text">{dayInWords}</p>
                        <p>{day}<sup>{getOrdinalSuffix(day)}</sup></p>
                        <p>{monthInWords}</p>
                    </div>
                    <div>
                        <h5 className="card-title">{truncate(title, 20)}</h5>
                        <p className="card-text">Available tickets:{tickets}</p>
                    </div>


                </div>
            </div>
        </>
    )
}
export default EventCard;
