import React, {useState} from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";
import '../css/AddEvent.css';


function AddEvent({events}){
    const [ formData, setFormData ] = useState({
        username: "",
        password: ""
      });

    const { user } = useContext(UserContext)

    const handleChange = (event) => {
        const { name, value } = event.target;

        setFormData({ ...formData, [name]: value });
      };

    const handleSubmit = (e) =>{
        e.preventDefault();
        const formData = new FormData()


        fetch("/drafts",{
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(event)
        })
        .then((res) => {
            if (res.ok){
               setFormData('')
            }
            else{
                // imput error message here
            }
        })
        .catch((error) =>{
            console.log(error);

        });
    };


    return(
        <div className="add-Event">
            {user?.admin?<>
                <form onSubmit={handleSubmit}>
                    <div className="card-addevent">
                    <h3>Create an Event</h3>
                    <input className="form-control"
                        type="text" 
                        placeholder="Title" 
                        name="Title" 
                        value={title}   
                        onChange={handleChange}                 
                        required/>
                        <br/>

                        <input className="form-control"
                        type="text"
                        placeholder="image"
                        name="image" 
                        value={image}  
                        onChange={handleChange}                
                        required/>
                        <br/> 

                        <div>
                        <select value={category} onChange={handleChange} className="form-control">
                            <option value="">Choose a category</option>
                            <option value="Music">Music</option>
                            <option value="Sports">Sports</option>
                            <option value="Motor show">Motor show</option>
                            <option value="Culture">Culture</option>
                            <option value="Theatre plays">Theatre plays</option>
                        </select>
                        </div>
                        <br/>


                        <input className="form-control"
                        type="text"
                        placeholder="Hosted_by"
                        name="Hosted_by"  
                        value={hosted_by}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Featuring"
                        name="featuring"  
                        value={featuring}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Dress_code"
                        name="Dress_code"  
                        value={dress_code}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Loaction"
                        name="Loaction"  
                        value={location}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="Date"
                        placeholder="Date"
                        name="Date"  
                        value={date}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="time"
                        placeholder="Time"
                        name="Time"  
                        value={time}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="tickets"
                        name="tickets"  
                        value={tickets}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Price"
                        name="Price"  
                        value={price}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <textarea className="form-control"
                        type="text"
                        placeholder="Description"
                        name="Description"  
                        value={category}
                        onChange={(e) => setDescription(e.target.value)}                 
                        required/>
                        <br/> 

                        <button className="btn sign btn-primary" type="submit">Create Event</button>
                    </div>

                </form>
            </>:<h3 style={{marginLeft:"35%"}}>Login to add an event</h3>}
        </div>
    )
}
export default AddEvent;
