import React from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";
import '../css/Home.css';


function Home( {events, handleSearch, search }){
    const { user } = useContext(UserContext)
    console.log(events);

    return(
        <div className="body">
            <div className="banner">
                <div className="heading">
                    <h1>At the heart of the best events</h1>
                </div>



                <form action="" className="searchbar">
                    <div>
                        <i class="fa fa-search" aria-hidden="true"></i>
                        <input type="search" required className="icon" name = "search" value = {search} onChange={handleSearch} placeholder = "Search event by category"></input>
                    </div>
                </form>
            </div>








      {user && !user?.admin ? <h1>Home customer</h1> : ''}
       { user?.admin ? <h1>Home organizer </h1> : ''}
        </div>
    )
}
export default Home;
