import React, { useEffect, useState} from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";
import {useParams, NavLink} from 'react-router-dom'


function CustomerEvents({ events }){
    const { user } = useContext(UserContext)
    const [customerEvents, setCustomerEvents] = useState([])
    const [isLoaded, setIsLoaded] = useState(false)
    const { id } = useParams();
    const [image, title, location, date] = events


    useEffect(() => {       
        fetch(`/customers/${id}/events`)
        .then((res) => res.json())
        .then((events) => {
            setCustomerEvents(events)
            setIsLoaded(true)
        })

    }, [id])

    if (!isLoaded) return <h2>Loading...</h2>

     {events.map(event => event )}

    const dates = new Date(date);
    const dayInWords = dates.toLocaleString("default", { weekday: "long" }).slice(0,3) // "WED"
    const day = dates.toLocaleString("default", { day: "numeric" })// "15"
    const monthInWords = dates.toLocaleString("default", { month: "long" }).slice(0,3) // "APR"


    function getOrdinalSuffix(day) {
        const j = day % 10, k = day % 100;
        if (j === 1 && k !== 11) {
          return "st";
        }
        if (j === 2 && k !== 12) {
          return "nd";
        }
        if (j === 3 && k !== 13) {
          return "rd";
        }
        return "th";
      }

      function truncate(str, n) {
        return str?.length > n ? str.substr(0, n - 1) + "..." : str;
      }


    return(
        <>
       <div className="card " style={{ width: '18rem'}}>
            <NavLink  to={`/events/${eventId}`}>
                <img src={image} className="card-img-top"alt={title} />
                </NavLink>
                <div className="card-body">                    
                    <div className="title-like">
                        <h5 className="card-title">{truncate(title, 20)}</h5>
                        <div className="event-like">


                         </div>
                         </div>
                        <div className="date-box">                       
                            <span>{dayInWords}, {monthInWords} {day}<sup>{getOrdinalSuffix(day)}</sup> <br/>
                            <label>{location}</label>
                            </span>                        

                        </div>

                </div>
            </div>
        </>
    )
}
export default CustomerEvents;
