import React, {useState} from "react"
import { useNavigate } from 'react-router-dom';
import './SignUp.css';


function SignUp({ setUser }){

    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [passwordConfirmation, setPasswordConfirmation] = useState("");
    const [errors, setErrors] = useState(null)
    const navigate = useNavigate();

    function handleSubmit(e){
        e.preventDefault();

        const newSignup = {
            username: username,
            email: email,
            password: password,
            password_confirmation: passwordConfirmation
        }

        if (username.length < 4) {
            setErrors("Username must be at least 4 characters long!!");            
          } 
          else if (password.length < 4 ){
            setErrors("Password must be at least 4 characters long!!"); 
          }
          else if (!password.includes('@')){
            setErrors("Password should include an @")
          }
          else if (password !== passwordConfirmation) {
            setErrors("Passwords do not match");            
          } 
          else{
            fetch('/organizers')
          } 
    }

    return(
        <>
        <div className="signup">
        <div className="card-signup">
        <form onSubmit = {handleSubmit}>

            <h4>Customer Sign Up</h4>

            <input className="form-control" 
                type="text" 
                placeholder="Enter Username"
                value = {username} 
                name="username" 
                required
                onChange ={(e) => setUsername(e.target.value)}/>
            <br/>

            <input className="form-control"
                type="email" 
                placeholder="Enter Your Email" 
                name="email" 
                value={email}
                onChange ={(e) => setEmail(e.target.value)}
                required/>
            <br/>

            <input className="form-control" 
                type="password" 
                placeholder="Enter Password" 
                name ='password'
                value = {password} 
                required
                onChange ={(e) => setPassword(e.target.value)}/>
            <br/>

            <input className="form-control"
                type="password"
                placeholder="Match Your Password"
                name="passwordConfirmation" 
                value = {passwordConfirmation}
                onChange ={(e) =>  setPasswordConfirmation(e.target.value)}
                required/>
            <br/>

            <p style={{color: 'red'}}>{errors}</p>

            <button className="btn login btn-primary" type="submit">Create Account</button>
        </form>
        </div>  




        <div className="card-signup2">
        <h4>Organiser Sign Up</h4>
        <input className="form-control" type="text" placeholder="Enter Username" name="uname" required/><br/>
        <input className="form-control" type="email" placeholder="Enter Your Email" name="uname" required/><br/>
        <input className="form-control" type="password" placeholder="Enter Password" name="psw" required/><br/>
        <input className="form-control" type="password" placeholder="Enter Password" name="psw" required/><br/>
        <button className="btn login btn-primary" type="submit">Create Account</button>
        </div> 
        </div>     
         </>
    )
}
export default SignUp;
