import React, {useState} from "react";
import { NavLink } from "react-router-dom";
// import { useContext } from "react";
// import { UserContext } from "../context/user";
import '../css/EventCard.css';


function EventCard({ event }) {
    const {image, title, date, location} = event
    const [liked, setLiked] = useState(false) 

    function truncate(str, n) {
        return str?.length > n ? str.substr(0, n - 1) + "..." : str;
      }

      const likedEvent = {
    customer_id: user? user.id : 1, event_id: event.id
      }

      function handleAddEvent() {
        fetch('/customer_events', {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
          body: JSON.stringify(likedEvent)
        })
        setLiked(true)
      }

      let selectedEventId;    

      user ? selectedEventId = user.customer_events.map(customerEvent => customerEvent.event).map(customerEvent => customerEvent.id) : selectedEventId = []



    const dates = new Date(date);
    const dayInWords = dates.toLocaleString("default", { weekday: "long" }).slice(0,3) // "WED"
    const day = dates.toLocaleString("default", { day: "numeric" })// "15"
    const monthInWords = dates.toLocaleString("default", { month: "long" }).slice(0,3) // "APR"


    function getOrdinalSuffix(day) {
        const j = day % 10, k = day % 100;
        if (j === 1 && k !== 11) {
          return "st";
        }
        if (j === 2 && k !== 12) {
          return "nd";
        }
        if (j === 3 && k !== 13) {
          return "rd";
        }
        return "th";
      }



    return (
        <>
            <div className="card " style={{ width: '18rem'}}>
            <NavLink  to={`/events/${event.id}`}>
                <img src={image} className="card-img-top"alt={image} />
                </NavLink>
                <div className="card-body">                    
                    <div>
                        <h5 className="card-title">{truncate(title, 20)}</h5>
                        <div className="date-box">                       
                            <span>{dayInWords}, {monthInWords} {day}<sup>{getOrdinalSuffix(day)}</sup></span>                        
                        </div>
                        <label>{location.toUpperCase()}</label>                        
                    </div>

                    {user ?  selectedAlbumId.indexOf(id) !== -1 || liked ? <i className="fa-solid fa-heart albumheart liked" onClick={handleAddAlbum}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-heart-fill" viewBox="0 0 16 16">
              <path fillRule="evenodd" d="M8 1.314C12.438-3.248 23.534 4.735 8 15-7.534 4.736 3.562-3.248 8 1.314z"/>
            </svg></i> : <i className="fa-solid fa-heart albumheart" onClick={handleAddAlbum}><svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-heart-fill" viewBox="0 0 16 16">
  <path fillRule="evenodd" d="M8 1.314C12.438-3.248 23.534 4.735 8 15-7.534 4.736 3.562-3.248 8 1.314z"/>
</svg></i>  : null}


                </div>
            </div>
        </>
    )
}
export default EventCard;
