import React, { useState } from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";


function PaymentForm({amount}){
    const { user } = useContext(UserContext) 
    const [phoneNumber, setPhoneNumber] = useState("")


    // let phone_number, amount;
    const formData = {
        phone_number : phoneNumber,
        amount : amount,
        customer_id: user?.id,
    }

    fetch('https://tamasha.onrender.com/bought_events', {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify(formData)
    })


    return(
        <div>
        {user ?  <>
        
            <h3 style={{marginLeft: "580px"}}>Enter Your Details</h3>
                    <form >
                        <div >
                        <label>Phone Number</label>        
                        <input className="form-control"
                        type="tel"
                        placeholder="254xxxxxxxxx"
                        pattern="[0-9]{12}"
                        maxLength='12'
                        minLength= '12'
                        name="phone" 
                        value={phoneNumber}
                        onChange={(e)=> setPhoneNumber(e.target.value)}                                                              
                        required/><br/>

                        
                        <button className="btn btn-danger pay" type="submit">Proceed To Pay</button>
                        </div>                    
           
                    </form>
            
            
            </> 
        : <h3 style={{marginLeft:"35%"}}>Login to access payment details</h3> }
        </div>
      
    )
}
export default PaymentForm;
