import React, { useState} from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";


function AddEventForm( {onAddDrafts} ){

    const { user } = useContext(UserContext)
    const [title, setTitle] = useState("")
    const [image, setImage] = useState("")
    const [category, setCategory] = useState("")
    const [description, setDescription] = useState("")
    const [hostedBy, setHostedBy] = useState("")
    const [featuring, setFeaturing] = useState("")
    const [dressCode, setDressCode] =useState("")
    const [location, setLocation] = useState("")
    const [date, setDate] = useState("")
    const [time, setTime] = useState("")
    const [tickets, setTickets] = useState("")
    const [price, setPrice] =useState("")


      const handleSubmit = (e) =>{
        e.preventDefault();
        const formData = new FormData();
        formData.append("draft[title]", title);
        formData.append("draft[category]", category);
        formData.append("draft[hostedBy]", hostedBy);
        formData.append("draft[featuring]", featuring);
        formData.append("draft[dressCode]", dressCode);
        formData.append("draft[location]", location);
        formData.append("draft[date]", date);
        formData.append("draft[time]", time);
        formData.append("draft[tickets]", tickets);
        formData.append("draft[price]", price);
        formData.append("draft[description]", description);

          console.log(formData);    
        fetch("/drafts",{
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: formData
        })   
        .then((res) => res.json())  
        .then((data) => onAddDrafts(data))   
        // setFormData({
        //     title: '',
        //     image: '',
        //     category: '',
        //     description: '',
        //     hosted_by: '',
        //     featuring: '',
        //     dress_code: '',
        //     location: '',
        //     date: '',
        //     time: '',
        //     tickets: '',
        //     price: '',
        //   });
            // else{
            //     // imput error message here
            // }
        // })
        // .catch((error) =>{
        //     console.log(error);

        // });
    };

    return(
        <>
        <div className="add-Event">
            {user?.admin?<>
                <form onSubmit={handleSubmit}>
                    <div className="card-addevent">
                    <h3>Create an Event</h3>
                    <input className="form-control"
                        type="text" 
                        placeholder="Title" 
                        name="title" 
                        value={title}   
                        onChange={(e) => setTitle(e.target.value)}                 
                        required/>
                        <br/>

                        <input className="form-control"
                        type="text"
                        placeholder="Image"
                        name="image" 
                        value={image}  
                        onChange={(e) => setImage(e.target.value)}               
                        required/>
                        <br/> 

                        <div>
                        <select value={category} onChange={(e) => setCategory(e.target.value)} className="form-control" name='category'>
                            <option value="">Choose a category</option>
                            <option value="Music">Music</option>
                            <option value="Sports">Sports</option>
                            <option value="Motor show">Motor show</option>
                            <option value="Culture">Culture</option>
                            <option value="Theatre plays">Theatre plays</option>
                        </select>
                        </div>
                        <br/>


                        <input className="form-control"
                        type="text"
                        placeholder="Hosted by?"
                        name="hosted_by"  
                        value={hostedBy}
                        onChange={(e) => setHostedBy(e.target.value)}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Featuring"
                        name="featuring"  
                        value={featuring}
                        onChange={(e) => setFeaturing(e.target.value)}              
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Dress code"
                        name="dress_code"  
                        value={dressCode}
                        onChange={(e) => setDressCode(e.target.value)}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Location"
                        name="location"  
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}                
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="date"
                        placeholder="Date"
                        name="date"  
                        value={date}
                        onChange={(e) => setDate(e.target.value)}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="time"
                        placeholder="Time"
                        name="time"  
                        value={time}
                        onChange={(e) => setTime(e.target.value)}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="number"
                        placeholder="Tickets"
                        name="tickets"  
                        value={tickets}
                       onChange={(e) => setTickets(e.target.value)}                
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="number"
                        placeholder="Price"
                        name="price"  
                        value={price}
                       onChange={(e) => setPrice(e.target.value)}                
                        required/>
                        <br/> 

                        <textarea className="form-control"
                        type="text"
                        placeholder="Description"
                        name="description"  
                        value={description}
                       onChange={(e) => setDescription(e.target.value)}                
                        required/>
                        <br/> 

                        <button className="btn sign btn-primary" type="submit">Create Event</button>
                    </div>

                </form>
            </>:<h3 style={{marginLeft:"35%"}}>Login to add an event</h3>}
        </div>

        </>
    )
}
export default AddEventForm;
