import React, { useEffect, useState} from "react";
import { useContext } from "react";
import { UserContext } from "../context/user";
import '../css/AddEvent.css';


function AddEvent(){
       console.log(drafts);
       const [drafts, setDrafts] = useState([])

       useEffect(() => {
           fetch('/drafts')
             .then((res) => res.json())
             .then((data) => setDrafts(data))
         }, [])

         console.log(drafts);

    const { user } = useContext(UserContext)
    const [formData, setFormData] = useState({
        title: '',
        image: '',
        category: '',
        description: '',
        hosted_by: '',
        featuring: '',
        dress_code: '',
        location: '',
        date: '',
        time: '',
        tickets: '',
        price: '',
      });


    const handleChange = (e) => {
        setFormData((prevState) => ({
          ...prevState,
          [e.target.name]: e.target.value,
        }));
      };

      const handleAddDrafts = (newDraft) =>{
        const updatedDraft = [...drafts, newDraft]
        setDrafts(updatedDraft)  
      }

    const handleSubmit = (e) =>{
        e.preventDefault();

        fetch("/drafts",{
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({...formData, organizer_id: user?.id})
        })   
        .then((res) => res.json())  
        .then((data) => handleAddDrafts(data))   
        // setFormData({
        //     title: '',
        //     image: '',
        //     category: '',
        //     description: '',
        //     hosted_by: '',
        //     featuring: '',
        //     dress_code: '',
        //     location: '',
        //     date: '',
        //     time: '',
        //     tickets: '',
        //     price: '',
        //   });
            // else{
            //     // imput error message here
            // }
        // })
        // .catch((error) =>{
        //     console.log(error);

        // });
    };


    return(
        <div className="add-Event">
            {user?.admin?<>
                <form onSubmit={handleSubmit}>
                    <div className="card-addevent">
                    <h3>Create an Event</h3>
                    <input className="form-control"
                        type="text" 
                        placeholder="Title" 
                        name="title" 
                        value={formData.title}   
                        onChange={handleChange}                 
                        required/>
                        <br/>

                        <input className="form-control"
                        type="text"
                        placeholder="Image"
                        name="image" 
                        value={formData.image}  
                        onChange={handleChange}                
                        required/>
                        <br/> 

                        <div>
                        <select value={formData.category} onChange={handleChange} className="form-control" name='category'>
                            <option value="">Choose a category</option>
                            <option value="Music">Music</option>
                            <option value="Sports">Sports</option>
                            <option value="Motor show">Motor show</option>
                            <option value="Culture">Culture</option>
                            <option value="Theatre plays">Theatre plays</option>
                        </select>
                        </div>
                        <br/>


                        <input className="form-control"
                        type="text"
                        placeholder="Hosted by?"
                        name="hosted_by"  
                        value={formData.hosted_by}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Featuring"
                        name="featuring"  
                        value={formData.featuring}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Dress code"
                        name="dress_code"  
                        value={formData.dress_code}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="text"
                        placeholder="Location"
                        name="location"  
                        value={formData.location}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="date"
                        placeholder="Date"
                        name="date"  
                        value={formData.date}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="time"
                        placeholder="Time"
                        name="time"  
                        value={formData.time}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="number"
                        placeholder="Tickets"
                        name="tickets"  
                        value={formData.tickets}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <input className="form-control"
                        type="number"
                        placeholder="Price"
                        name="price"  
                        value={formData.price}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <textarea className="form-control"
                        type="text"
                        placeholder="Description"
                        name="description"  
                        value={formData.description}
                        onChange={handleChange}                 
                        required/>
                        <br/> 

                        <button className="btn sign btn-primary" type="submit">Create Event</button>
                    </div>

                </form>
            </>:<h3 style={{marginLeft:"35%"}}>Login to add an event</h3>}
        </div>
    )
}
export default AddEvent;
